from tkinter import *root = Tk()root.title('Credit Card Authentication')root.geometry('500x500')root.configure(background='yellow')inputBox = Entry(root)inputBox.place(relx=0.5,rely=0.3,anchor=CENTER)img = ImageTk.PhotoImg(Image.open('credit.jpg'))label = Label(root,img='img')def authentication():    inputValue = inputBox.get    messagebox.showinfo('Error Warning','the credit card is accepted')    btn = Button(root,title='check credit card',command=authentication)btn.place(relx=0.5,rely=0.4,anchor=CENTER)mainloop()